import {Typography,Button, CardContent,CardActions,Card} from '@mui/material';

const ItemInfoPage = ({category, title, price, desc, action}) => {

  return (
    <Card sx={{ minWidth: 275 }}>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>{category}</Typography>
        <Typography variant="h5" component="div">{title}</Typography>
        <Typography sx={{ mb: 1.5 }} color="text.secondary">{price}$</Typography>
        <Typography variant="body2">{desc}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={(id)=>action} >Add to cart</Button>
      </CardActions>
    </Card>
  )
}

export default ItemInfoPage
