import { createSlice} from "@reduxjs/toolkit";

export const itemsSlice = createSlice({
    name: 'items',
    initialState: [],
    reducers:{
        getItems:(state,action)=>{
            state.value = action.payload
        }
    }
})

export default itemsSlice.reducer;
