import { useToggle } from "../../hooks/useToggle";
import { createContext } from "react";

const AccordionContext= createContext()

const Accordion = ({ title, content }) => {

   

    const { status:expand, toggleStatus:toogleExpand } = useToggle(false);
    console.log(expand)

    return (

        <div>
            <AccordionHeader title={title} expand={expand} toggleExpand={toogleExpand}/>
            <AccordionContent content={content} expand={expand}/>
        </div>
    )

};

const AccordionHeader = ({title, expand, toggleExpand}) => {
    return(
    <button onClick={toggleExpand}>
        {title} <span>{expand ? '-' : '+'}</span>
    </button>
    )
};

const AccordionContent = ({content,expand}) => {
    if(expand){
        return(
    <div>
        {content}
    </div>
    )
    }
    
}

export default Accordion
