import { Typography, Button, Grid, Card, CardMedia } from '@mui/material';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { Box } from '@mui/system';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import LoadingBar from './LoadingBar';

const ItemInfoPage = ({ url }) => {

    const itemId = useParams();
    const [item, setItem] = useState(null)

    const addToCart = (item) => {
        console.log(item)
    }

    useEffect(() => {
        const getItem = async () => {
            try {
                let res = await fetch(`${url}/${itemId.id}`);
                let item = await res.json();
                console.log(item)
                setItem(item);
            } catch (err) {
                console.log('error:' + err)
            }
        }
        getItem();
    }, [itemId, url])


    if (item !== null) {
        return (
            <Card sx={{ height: '800px', p: 2 }}>
                <Box sx={{ height: '5%' }}>
                    <Typography variant='body'
                        gutterBottom
                        sx={{
                            textTransform: 'uppercase'
                            , borderBottom: 'solid 1.5px #ccc',
                            p: 0.5,
                            m: 0.5
                        }}
                    >{item.category}</Typography>
                </Box>
                <Grid container spacing={2} sx={{ height: '95%' }}>
                    <Grid item xs={6}>
                        <Card raised sx={{ height: '100%', width: "100%" }}>
                            <CardMedia
                                component="img"
                                image={item.image}
                                alt={item.title}
                                sx={{
                                    '&.MuiCardMedia-img': {
                                        objectFit: 'contain',
                                        height: '100%',
                                        width: '100%',
                                    }
                                }}
                            />
                        </Card>
                    </Grid>
                    <Grid item xs={6}>
                        <Box sx={{ height: '100%' }}>
                            <Box sx={{ height: '20%', mb: 2 }}>
                                <Typography variant="h3" component="div">{item.title}</Typography>
                            </Box>
                            <Box sx={{ height: '20%', mb: 2 }}>
                                <Typography variant='h6' sx={{ mb: 1.5 }} >$ {item.price}</Typography>
                                <Box sx={{ display: 'flex', flecFlow: 'row noWrap', alignContent: 'center', mb: 1 }}>
                                    <Typography variant='subtitle2'
                                        sx={{ p: '1px' }} >Rating: {item.rating.rate}
                                    </Typography>
                                    <StarBorderIcon color='warning' />
                                </Box>

                                <Button variant="contained" color='success' onClick={() => addToCart(item)} >Add to cart</Button>
                            </Box>
                            <Box sx={{ height: '30%', mb: 2, display: 'flex', flexDirection: 'column' }}>
                                <Typography variant="h5">Description:</Typography>
                                <Typography variant="body">{item.description}</Typography>
                            </Box>
                        </Box>
                    </Grid>

                </Grid>

            </Card>
        )
    } else {
        <LoadingBar/>
    }


}

export default ItemInfoPage
