import { Box, ImageList, ImageListItem, ImageListItemBar, styled } from '@mui/material'
import React from 'react'
import image1 from '../../img/desktop-cat-bolsos.webp'
import image2 from '../../img/desktop-cat-carteras.webp'
import image3 from '../../img/desktop-cat-viajes.webp'
import image4 from '../../img/desktop-cat-hombre.webp'

const BannerCateg = ({ responsive }) => {

    const BannerImage = styled('img')(() => ({
        width: '100%',
        objectFit: 'cover',
    }))

    const [bannerCategories]=useState([
        cat1={title:'bags', image:{image1}},
        cat2={title:'handbags', image:{image2}},
        cat3={title:'travel', image:{image3}},
        cat4={title:'men', image:{image4}},
    ]     
    )

    return (
        <Box >
            <ImageList sx={{
                display: 'flex', flexDirection: 'row', flexWrap: 'Wrap', width: '100%',
                justifyContent: 'space-around'
            }}>
                <ImageListItem sx={{width: (responsive.desk) ? '20%' : '45%' }}>
                    <BannerImage src={image1} alt='bolsos' />
                    <ImageListItemBar title="bolsos"
                        sx={{
                            position: 'relative',
                            textAlign: 'center',
                            textTransform: 'uppercase',
                            bottom: '50%',
                            background:'rgba(200, 200, 205, 0.4)',
                            '&>.MuiImageListItemBar-titleWrap':{
                                fontWeight:'bolder',
                                fontFamily:'Miller Bold',
                                padding:0,
                                color:'#F8F8FF',
                                fontSize:(responsive.desk)?'2rem':'6vw',
                                
                            },
                            '&>.MuiImageListItemBar-title':{
                                lineHeight:'none',
                            }
                        }} />
                </ImageListItem>
                <ImageListItem sx={{ width: (responsive.desk) ? '20%' : '45%' }}>
                    <BannerImage src={image2} alt='carteras' />
                    <ImageListItemBar title="carteras" sx={{
                            position: 'relative',
                            textAlign: 'center',
                            textTransform: 'uppercase',
                            bottom: '50%',
                            background:'rgba(200, 200, 205, 0.4)',
                            '&>.MuiImageListItemBar-titleWrap':{
                                fontWeight:'bolder',
                                fontFamily:'Miller Bold',
                                padding:0,
                                color:'#F8F8FF',
                                fontSize:(responsive.desk)?'2rem':'6vw',
                            },
                            '&>.MuiImageListItemBar-title':{
                                lineHeight:'none'
                            }
                        }}/>
                </ImageListItem>
                <ImageListItem sx={{ width: (responsive.desk) ? '20%' : '45%' }}>
                    <BannerImage src={image3} alt='viajes' />
                    <ImageListItemBar title="viajes" sx={{
                            position: 'relative',
                            textAlign: 'center',
                            textTransform: 'uppercase',
                            bottom: '50%',
                            background:'rgba(200, 200, 205, 0.4)',
                            '&>.MuiImageListItemBar-titleWrap':{
                                fontWeight:'bolder',
                                fontFamily:'Miller Bold',
                                padding:0,
                                color:'#F8F8FF',
                                fontSize:(responsive.desk)?'2rem':'6vw',
                            },
                            '&>.MuiImageListItemBar-title':{
                                lineHeight:'none'
                            }
                        }}/>
                </ImageListItem>
                
                <ImageListItem sx={{ width: (responsive.desk) ? '20%' : '45%' }}>
                    <BannerImage src={image4} alt='hombre' />
                    <ImageListItemBar title="hombre" sx={{
                            position: 'relative',
                            textAlign: 'center',
                            textTransform: 'uppercase',
                            bottom: '50%',
                            background:'rgba(200, 200, 205, 0.4)',
                            '&>.MuiImageListItemBar-titleWrap':{
                                fontWeight:'bolder',
                                fontFamily:'Miller Bold',
                                padding:0,
                                color:'#F8F8FF',
                                fontSize:(responsive.desk)?'2rem':'6vw'
                            },
                            '&>.MuiImageListItemBar-title':{
                                lineHeight:'none'
                            }
                        }}/>
                </ImageListItem>
            </ImageList>
        </Box>
    )
}

export default BannerCateg
