import NavLink from "../styledComponent/NavLink";
import { AppBar, Toolbar, IconButton, Typography, List, ListItem, Grid, Menu, MenuItem,TextField  } from "@mui/material";
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import { Box } from "@mui/system";
import { ShoppingBagOutlined, PersonOutlineOutlined, FavoriteBorderOutlined } from '@mui/icons-material';
import { useState } from "react";

const HeaderTablet = ({ title, navMenu }) => {

    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };


    return (
        <AppBar component='div' color='inherit' sx={{ boxShadow: 'none', position: 'static' }}>
            <Toolbar>
                <Grid container spacing={2}
                    sx={{
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        padding: '16px',
                        margin: '0',
                        '&>.MuiGrid-item': {
                            padding: 0
                        }
                    }}>
                    <Grid item xs={2}>
                        <Box sx={{ display: "flex", flexDirection: "row nowrap" }}>
                            <IconButton id="basic-button"
                                aria-controls={open ? 'basic-menu' : undefined}
                                aria-haspopup="true"
                                aria-expanded={open ? 'true' : undefined}
                                onClick={handleClick}>
                                <MenuIcon />

                            </IconButton>
                            <Menu
                                id="basic-menu"
                                anchorEl={anchorEl}
                                open={open}
                                onClose={handleClose}
                                MenuListProps={{
                                    'aria-labelledby': 'basic-button',
                                }}
                            >
                                {navMenu.map((itemMenu) => {
                                    return (
                                        <MenuItem key={itemMenu}
                                            onClick={handleClose}
                                            sx={{ textTransform: 'uppercase' }}
                                        >
                                            <NavLink to={'/' + itemMenu}>{itemMenu}</NavLink>
                                        </MenuItem>
                                    )
                                })}

                            </Menu>
                            <Box sx={{ display: 'flex', flexDirection: 'row',alignItems:'center', height: '24px' }}>
                                <IconButton>
                                    <SearchIcon />
                                </IconButton>
                                <TextField placeholder='Search' color='primary' variant='filled' 
                                sx={{width:'140px',height:'inherit','&>.MuiFilledInput-root:before':{
                                    borderBottom:'none'
                                }}}/>
                            </Box>
                        </Box>
                    </Grid>
                    <Grid item xs={8}>
                        <Box>
                            <Typography component="h1" sx={{
                                textAlign: 'center', textTransform: 'uppercase',
                                fontFamily: 'Aldrich', letterSpacing: '6px', fontSize: '3.2vw'
                            }}>{title}</Typography>
                        </Box>
                    </Grid>
                    <Grid item xs={2}>
                        <Box>
                            <List sx={{ display: "flex", flexDirection: "row nowrap" }}>
                                <ListItem>
                                    <IconButton>
                                        <FavoriteBorderOutlined />
                                    </IconButton>
                                </ListItem>
                                <ListItem>
                                    <IconButton>
                                        <PersonOutlineOutlined />
                                    </IconButton>
                                </ListItem>
                                <ListItem>
                                    <IconButton>
                                        <ShoppingBagOutlined />
                                    </IconButton>
                                </ListItem>
                            </List>
                        </Box>
                    </Grid>
                </Grid>
            </Toolbar>

        </AppBar>
    )
}

export default HeaderTablet
