import React, { useState } from 'react'
import TopHeader from './deskComponents/TopHeader'
import MediumHeader from './deskComponents/MediumHeader'
import BottomHeader from './deskComponents/BottomHeader'
import { Card } from '@mui/material'
import HeaderMobile from './mobileComponents/HeaderMobile'
import HeaderTablet from './tabletComponents/HeaderTablet'
import { styled } from '@mui/material/styles'


const Header = ({ responsive }) => {

  

  const HeaderCard = styled(Card)(()=>({
    display:'flex',
    flexDirection: 'column', 
    alignItems: 'center'
  }))

  const[navMenu,setNavMenu]=useState(['home','viaje','bolsos','carteras','accesorios','hombre','rebajas'])
  const [text] = useState(['A TIENDA! el tiempo de entrega para los pedidos es de 4/7 dias',
    'Ampliado el plazo para cambios y devoluciones hasta el 20/03/2023',
    'Los tiempos de entrega pueden estar sujetos a modificaciones durante las rebajas'])


  if (responsive.mobile) {
    return (
      <HeaderCard component='header'>
        <HeaderMobile title='PacoMartinez'/>
      </HeaderCard>

    )
  } else if (responsive.tablet) {
    return (
      <HeaderCard component='header'>
        <HeaderTablet title='PacoMartinez'/>
      </HeaderCard>
    )
  } else if (responsive.desk) {
    return (
      <HeaderCard component='header'>
        <TopHeader textArr={text} />
        <MediumHeader title='PacoMartinez' />
        <BottomHeader/>
      </HeaderCard>
    )
  }


}

export default Header
