import React, { useState } from 'react'
import TopHeader from './deskComponents/TopHeader'
import MediumHeader from './deskComponents/MediumHeader'
import BottomHeader from './deskComponents/BottomHeader'
import HeaderMobile from './mobileComponents/HeaderMobile'
import HeaderTablet from './tabletComponents/HeaderTablet'
import { Card } from '@mui/material'
import { styled } from '@mui/material/styles'
import { useSelector } from 'react-redux';


const Header = ({ responsive }) => {

  const cartItems = useSelector((state) => state.cart.value)
  const cartItemsSize= cartItems.length;

  const HeaderCard = styled(Card)(()=>({
    display:'flex',
    flexDirection: 'column', 
    alignItems: 'center'
  }))

  const[navMenu,setNavMenu]=useState(['home','viaje','bolsos','carteras','accesorios','hombre','productos'])
  const [text] = useState(['A TIENDA! el tiempo de entrega para los pedidos es de 4/7 dias',
    'Ampliado el plazo para cambios y devoluciones hasta el 20/03/2023',
    'Los tiempos de entrega pueden estar sujetos a modificaciones durante las rebajas'])


  if (responsive.mobile) {
    return (
      <HeaderCard>
        <HeaderMobile title='PacoMartinez' navMenu={navMenu} cartItemsSize={cartItemsSize}/>
      </HeaderCard>

    )
  } else if (responsive.tablet) {
    return (
      <HeaderCard>
        <HeaderTablet title='PacoMartinez' navMenu={navMenu} cartItemsSize={cartItemsSize}/>
      </HeaderCard>
    )
  } else if (responsive.desk) {
    return (
      <HeaderCard>
        <TopHeader textArr={text} />
        <MediumHeader title='PacoMartinez' cartItemsSize={cartItemsSize} />
        <BottomHeader navMenu={navMenu}/>
      </HeaderCard>
    )
  }


}

export default Header
