import { CircularProgress, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState, useEffect } from 'react'
import ActionAreaCard from '../styledComponent/ActionAreaCard'
import NavLink from '../styledComponent/NavLink'


import PropTypes from 'prop-types';

const Rebajas = ({url}) => {
 
  const [items, setItems] = useState([])

  useEffect(() => {
    const getData = async () => {
      try {
        let data = await fetchList();
        setItems(data)
      } catch (err) {
        console.log('error:' + err)
      }
    }
    getData();
  }, [])

  //Get
  const fetchList = async () => {
    try {
      let res = await fetch(url);
      let list = await res.json();
      return list;
    } catch (err) {
      console.log('error:' + err)
    }
  }

  if (items.length > 0) {
    return (
      <Box sx={{ display: "flex", flexDirection: "row", flexWrap: "wrap", justifyContent: 'space-around' }}>

        {items.map((item) => {
          return (
            <NavLink to={`/rebajas/`+item.id}>
              <ActionAreaCard
                key={item.id}
                title={item.title}
                price={item.price}
                image={item.image}
                id={item.id}
              />
            </NavLink>
          )
        })}
      </Box>)

  } else {
    return (
      <Box sx={{height:'800px', display:'flex', justifyContent:'center', pt:4}}>
        <Typography variant='h6'>"loading..."</Typography>
        <Box sx={{ position: 'relative', display: 'inline-flex' }}>
      <CircularProgress variant="determinate" />
      <Box
        sx={{
          top: 0,
          left: 0,
          bottom: 0,
          right: 0,
          position: 'absolute',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        <Typography variant="caption" component="div" color="text.secondary">
          {`${Math.round(props.value)}%`}
        </Typography>
      </Box>
    </Box>
      </Box>
    )
  }



}

export default Rebajas
