import { useState } from 'react';
import { TextField, InputAdornment } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import { useToggle } from '../../hooks/useToggle';

const SearchBar = ({placeholder,value}) => {
    const [input, setInput] = useState(value)
    const { status: openSearch, toggleStatus: setOpenSearch } = useToggle(false)
    function handleClickSearch(value) {
        console.log(value)
        setInput('')
        setOpenSearch(false)
      }
    

    return (
        <TextField
            fullWidth
            
            placeholder={placeholder}
            color='primary'
            variant='filled'
            inputProps={{
                style: {
                    padding: '2px 5px ',
                }
            }}
            InputProps={{
                endAdornment: (
                    <InputAdornment position="end" onClick={() => handleClickSearch(input)} >
                        <SearchIcon />
                    </InputAdornment>
                ),
            }}
            sx={{
                display: 'flex',
                alignItems: 'ceter',
                '&>.MuiFilledInput-root:before': {
                    borderBottom: 'none'
                },
                '&>.MuiFilledInput-root:after': {
                    borderBottom: 'none'
                },
                '&>.css-hy97yo-MuiInputBase-root-MuiFilledInput-root:hover:not(.Mui-disabled, .Mui-error):before': { borderBottom: 'none' }
            }}
        />
    )
}

export default SearchBar
