import { useState } from "react";
import NavLink from "../styledComponent/NavLink";
import SearchBar from "../styledComponent/SearchBar";
import { Box } from "@mui/system";
import { AppBar, Toolbar, IconButton, Typography, List, ListItem, Grid, Menu, MenuItem} from "@mui/material";
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import { ShoppingBagOutlined, PersonOutlineOutlined, FavoriteBorderOutlined } from '@mui/icons-material';


const HeaderTablet = ({ title, navMenu }) => {

    const [searchTextField, setSearchTextField] = useState('null')
    const [openSearch, setOpenSearch] = useState(false)
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    function handleClickSearch(value) {
        console.log(value)
        setSearchTextField('')
    };



    return (
        <AppBar component='div' color='inherit' sx={{ boxShadow: 'none', position: 'static' }}>
            <Toolbar>
                <Grid container spacing={2}
                    sx={{
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        padding: '16px',
                        margin: '0',
                        '&>.MuiGrid-item': {
                            padding: 0
                        }
                    }}>
                    <Grid item xs={2}>
                        {!openSearch ?
                            <Box sx={{ display: "flex", flexDirection: "row nowrap", alignItems: 'center' }}>

                                <IconButton id="basic-button"
                                    aria-controls={open ? 'basic-menu' : undefined}
                                    aria-haspopup="true"
                                    aria-expanded={open ? 'true' : undefined}
                                    onClick={handleClick}>
                                    <MenuIcon />

                                </IconButton>
                                <Menu
                                    id="basic-menu"
                                    anchorEl={anchorEl}
                                    open={open}
                                    onClose={handleClose}
                                    MenuListProps={{
                                        'aria-labelledby': 'basic-button',
                                    }}
                                >
                                    {navMenu.map((itemMenu) => {
                                        return (
                                            <MenuItem key={itemMenu}
                                                onClick={handleClose}
                                                sx={{ textTransform: 'uppercase' }}
                                            >
                                                <NavLink to={'/' + itemMenu}>{itemMenu}</NavLink>
                                            </MenuItem>
                                        )
                                    })}

                                </Menu>
                                <IconButton onClick={() => { setOpenSearch(true) }}>
                                    <SearchIcon />
                                </IconButton>
                            </Box>
                            :
                            <SearchBar toggleStatus={()=>toggleStatus(false)}/>
                        }


                    </Grid>
                    <Grid item xs={8}>
                        <Box>
                            <Typography component="h1" sx={{
                                textAlign: 'center', textTransform: 'uppercase',
                                fontFamily: 'Aldrich', letterSpacing: '6px', fontSize: '3.2vw'
                            }}>{title}</Typography>
                        </Box>
                    </Grid>
                    <Grid item xs={2}>
                        <Box>
                            <List sx={{ display: "flex", flexDirection: "row nowrap" }}>
                                <ListItem>
                                    <IconButton>
                                        <FavoriteBorderOutlined />
                                    </IconButton>
                                </ListItem>
                                <ListItem>
                                    <IconButton>
                                        <PersonOutlineOutlined />
                                    </IconButton>
                                </ListItem>
                                <ListItem>
                                    <IconButton>
                                        <ShoppingBagOutlined />
                                    </IconButton>
                                </ListItem>
                            </List>
                        </Box>
                    </Grid>
                </Grid>
            </Toolbar>

        </AppBar>
    )
}

export default HeaderTablet
