import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux'
import { getItems } from '../../features/cart'

import { Button, Card, Divider, Typography } from "@mui/material"
import { Box } from "@mui/system"
import { useState } from 'react';
import ItemCard from '../styledComponent/ItemCard';

const ShoppingCart = () => {

    const cartItems = useSelector((state)=>state.cart.value)
    const dispatch=useDispatch();

    const emptyMsg='Tu cesta se encuentra vacia'

  return (
    <Box sx={{minHeight:'800px'}}>
        <Card>
            <Box>
                <Typography>My Cesta</Typography>
            </Box>
            <Box>
                {!cartItems ?
                <Typography>{emptyMsg}</Typography>  
                :
                cartItems.map((item)=>{
                    <ItemCard item={item}/>  
                })
                
            }
            </Box>
            <Box>
                <Divider/>
                <Button>Continuar comprando</Button>
            </Box>
            <Box>
                <Typography>Total</Typography>
                <Divider/>
                <Button>Continuar</Button>
            </Box>
            
            
        </Card>
    </Box>
  )
}

export default ShoppingCart
