    const timer = setTimeout(() => {
      if (i>=textArr.length-1){
        setI(0)
        }else{
        setI(i+1);
      }
      setText(textArr[i])
    }, 10000);
    return () => clearTimeout(timer);
  }, [i,textArr]);

