import {Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState, useEffect } from 'react'
import ActionAreaCard from '../styledComponent/ActionAreaCard'

const Rebajas = () => {
  const url = 'https://fakestoreapi.com/products'
  const [items, setItems] = useState([])

  useEffect(() => {
    const getData = async () => {
      try {
        let data = await fetchList();
        setItems(data)
      } catch (err) {
        console.log('error:' + err)
      }


    }
    getData();
  }, [])

  //Get
  const fetchList = async () => {
    try {
      let res = await fetch(url);
      let list = await res.json();
      return list;
    } catch (err) {
      console.log('error:' + err)
    }
  }

  const openItem= async(id)=>{
   
      try {
        let res = await fetch(`${url}/${id}`);
        let item = await res.json();
        return item;
      } catch (err) {
        console.log('error:' + err)
      }
    
  }


  if (items.length > 0) {
    return (
      <Box sx={{display: "flex", flexDirection: "row",flexWrap:"wrap", justifyContent:'space-around' }}>

        {items.map((item) => {
          return(<ActionAreaCard 
            key={item.id} 
            title = {item.title} 
            price = {item.price} 
            image={item.image} 
            id={item.id}
            openSingleAction={(id)=>openItem(id)}/>)
      })}
      </Box>)

  } else {
    return (<Typography>"loading"</Typography>)
  }



}

export default Rebajas
