import { useSelector } from 'react-redux';

import { Button, Card, Divider, Typography, CardMedia, Box } from "@mui/material"
import { useEffect, useState } from 'react';
import StarBorderIcon from '@mui/icons-material/StarBorder';

const ShoppingCart = () => {

    const cartItems = useSelector((state) => state.cart.value)
    const [total,setTotal]=useState(0);

    useEffect(()=>{
        cartItems.map((item)=>{
            setTotal(prevTotal+Number(item.price))
        })
    },[cartItems])


    const [emptyMsg] = useState('Tu cesta se encuentra vacia')
    const [currency] = useState('$')
    console.log(cartItems)
    return (
        <Box sx={{ minHeight: '800px' }}>
            <Card>
                <Box>
                    <Typography>My Cesta</Typography>
                </Box>
                <Box sx={{ display: 'flex',justifyContent:'center', flexDirection:'column'}}>
                    {!cartItems ?
                        <Typography>{emptyMsg}</Typography>
                        :
                        cartItems.map((item) => {
                            return (
                                <CardMedia raised sx={{ 
                                border:'solid 2px #ccc',
                                borderRadius:2,
                                height: '200px',
                                m: 2, 
                                p: 1,
                                width:'90%',
                                display: 'flex',
                                flexFlow:'row noWrap',
                                justifyContent:'space-between'
                                }}>
                                    <Box sx={{width:'35%',p:1}}>
                                        <Card sx={{ height: '100%' }}>
                                            <CardMedia
                                                component="img"
                                                image={item.image}
                                                alt={item.title}
                                                sx={{
                                                    p:2,
                                                    height: '100%',
                                                    '&.MuiCardMedia-img': {
                                                        objectFit: 'contain',
                                                        height: '100%',
                                                        width: '100%',
                                                    }
                                                }}
                                            />
                                        </Card>
                                    </Box>

                                    <Box sx={{ height: '100%', width:'45%',p:1 }}>
                                        <Box sx={{ height: '55%', mb: 1 }}>
                                            <Typography variant="h5" component="div">{item.title}</Typography>
                                        </Box>
                                        <Box sx={{ height: '20%', mb: 1 }}>
                                            <Typography variant='h6' sx={{ mb: 1.5 }} >{currency} {item.price}</Typography>
                                            <Box sx={{ display: 'flex', flecFlow: 'row noWrap', alignContent: 'center', mb: 1 }}>
                                                <Typography variant='subtitle2'
                                                    sx={{ p: '1px' }} >Rating: {item.rating.rate}
                                                </Typography>
                                                <StarBorderIcon color='warning' />
                                            </Box>
                                        </Box>
                                    </Box>


                                </CardMedia>
                            )
                        })

                    }
                </Box>
                <Box>
                    <Divider />
                    <Button>Continuar comprando</Button>
                </Box>
                <Box>
                    <Typography>{total}</Typography>
                    <Divider />
                    <Button>Continuar</Button>
                </Box>


            </Card>
        </Box>
    )
}

export default ShoppingCart
