import { Card, CardContent, CardMedia, ImageListItem, ImageListItemBar, styled, Typography } from '@mui/material'

const CatBannerCard = ({ item, responsive }) => {


    const BannerImage = styled('img')(() => ({
        width: '100%',
        height:'100%',
        objectFit: 'contain',
    }))

    return (
        <Card raised sx={{height:'100%', width: (responsive.desk) ? '20%' : '45%' }}>

            <CardMedia image={item.image} title={item.title} sx={{
                width: '100%',
                height: '100%',
                objectFit: 'fill',
            }} />
            <CardContent>
                <Typography
                    sx={{
                   
                        textAlign: 'center',
                        textTransform: 'uppercase',
                        bottom: '50%',
                        background: 'rgba(200, 200, 205, 0.4)',
                        '&>.MuiImageListItemBar-titleWrap': {
                            fontWeight: 'bolder',
                            fontFamily: 'Miller Bold',
                            padding: 0,
                            color: '#F8F8FF',
                            fontSize: (responsive.desk) ? '2rem' : '6vw',

                        },
                        '&>.MuiImageListItemBar-title': {
                            lineHeight: 'none',
                        }
                    }}
                >
                    {item.title}
                </Typography>
            </CardContent>
        </Card>
    )
}

export default CatBannerCard
