import {LinearProgress, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState, useEffect } from 'react'
import ActionAreaCard from '../styledComponent/ActionAreaCard'
import NavLink from '../styledComponent/NavLink'

const Rebajas = ({ url }) => {

  const [items, setItems] = useState([])


  useEffect(() => {
    const getData = async () => {
      try {
        let data = await fetchList();
        setItems(data)
      } catch (err) {
        console.log('error:' + err)
      }
    }
    getData();
  }, [])

  //Get
  const fetchList = async () => {
    try {
      let res = await fetch(url);
      let list = await res.json();
      return list;
    } catch (err) {
      console.log('error:' + err)
    }
  }

  if (items.length > 0) {
    return (
      <Box sx={{ display: "flex", flexDirection: "row", flexWrap: "wrap", justifyContent: 'space-around' }}>

        {items.map((item) => {
          return (
            <NavLink to={`/rebajas/` + item.id}>
              <ActionAreaCard
                key={item.id}
                title={item.title}
                price={item.price}
                image={item.image}
                id={item.id}
              />
            </NavLink>
          )
        })}
      </Box>)

  } else {
    return (
      <Box sx={{ height: '800px', display: 'flex',flexDirection:'column', alignItems: 'center', pt: 6 }}>
        <Typography variant='h6'>LOADING...</Typography>
        <Box sx={{ width: '50%' }}>
           <LinearProgress color='primary'/>
        </Box>
       
      </Box>
    )
  }



}

export default Rebajas
