import {Card, ImageListItem, ImageListItemBar, styled } from '@mui/material'

const CatBannerCard = ({ item,responsive }) => {


        const BannerImage = styled('img')(() => ({
            width: '100%',
            height:'100%',
            objectFit: 'fill',
        }))

        return (
            <Card  raised sx={{width: (responsive.desk) ? '20%' : '45%' }}>
            <ImageListItem >
                <BannerImage src={item.image} alt={item.title} />
                <ImageListItemBar title={item.title}
                    sx={{
                        position: 'relative',
                        textAlign: 'center',
                        textTransform: 'uppercase',
                        bottom: '50%',
                        background: 'rgba(200, 200, 205, 0.4)',
                        '&>.MuiImageListItemBar-titleWrap': {
                            fontWeight: 'bolder',
                            fontFamily: 'Miller Bold',
                            padding: 0,
                            color: '#F8F8FF',
                            fontSize: (responsive.desk) ? '2rem' : '6vw',

                        },
                        '&>.MuiImageListItemBar-title': {
                            lineHeight: 'none',
                        }
                    }} />
            </ImageListItem>
            </Card>
        )
    }

    export default CatBannerCard
