import { Accordion, AccordionDetails, AccordionSummary, List, ListItem, ListItemText, Typography } from '@mui/material'
import AddIcon from '@mui/icons-material/Add';

const MobileFooter = ({ selectCountry, selected, allOptions, onChange }) => {
    return (
        allOptions.map((options, i) => {

            return (
                <Box sx={{ margin: '72px 0' }}>
                    <FormControl variant="standard" sx={{ m: 1, minWidth: 120, marging: '0', '&>.MuiInputBase-root': { marginTop: 0 } }}>
                        <InputLabel id="country-label" sx={{
                            padding: '0', transformOrigin: 'bottom',
                            lineHeight: '1.6rem', textTransform: 'uppercase'
                        }}></InputLabel>
                        <Select
                            labelId="country-label"
                            id="demo-simple-select-standard"
                            value={selected}
                            label="country-label"
                            onChange={(e) => { e.preventDefault(); onChange(e.target.value) }}
                        >
                            {selectCountry.map((country) => {
                                return <MenuItem key={country} value={country} sx={{ textTransform: 'uppercase' }}>{country}</MenuItem>
                            })}
                        </Select>
                    </FormControl>
                    <Accordion key={options}>
                        <AccordionSummary expandIcon={<AddIcon />}>
                            <Typography>{options[0]}</Typography>
                        </AccordionSummary>
                        <List>
                            {options.map((value, i) => {
                                if (i > 0) {
                                    return (<ListItem>
                                        <ListItemText primary={value} />
                                    </ListItem>)
                                }


                            })}

                        </List>
                        <AccordionDetails>

                        </AccordionDetails>
                    </Accordion>
                </Box>
            )

        })

    )
}

export default MobileFooter
