import { Box, MenuItem, List, ListItem, ListItemText, Divider, Select, InputLabel, FormControl } from '@mui/material'
import { styled } from '@mui/material/styles';

const DeskFooter = ({ selectCountry, selected, allOptions, onChange }) => {

  const StyledList = styled(List)(() => ({
    '&>:first-of-type': {
      textTransform: 'uppercase',
    }
  }))

  const StyledListItem = styled(ListItem)(() => ({
    '&>:first-of-type': { fontWeight: '600' }

  }))

  return (
    <Box sx={{ margin: '72px 0' }}>
      <Divider />
      <Box component='footer' sx={{ display: 'flex', flexFlow: 'row nowrap', justifyContent: 'space-around', marginTop: '24px' }}>
        <FormControl variant="standard" sx={{ m: 1, minWidth: 120, marging: '0', '&>.MuiInputBase-root': { marginTop: 0 } }}>
          <InputLabel id="country-label" sx={{
            padding: '0', transformOrigin: 'bottom',
            lineHeight: '1.6rem', textTransform: 'uppercase'
          }}></InputLabel>
          <Select
            labelId="country-label"
            id="demo-simple-select-standard"
            value={selected}
            label="country-label"
            onChange={(e) => { e.preventDefault(); onChange(e.target.value) }}
          >
            {selectCountry.map((country) => {
              return <MenuItem key={country} value={country} sx={{ textTransform: 'uppercase' }}>{country}</MenuItem>
            })}
          </Select>
        </FormControl>

        {allOptions.map((option) => {
          return(
            <StyledList key={option}>
           {option.map((value, index) => {
              if (index === 0) {
                return <StyledListItem key={index}>
                  <ListItemText disableTypography>{value}</ListItemText>
                </StyledListItem>
              } else {
                return <ListItem key={index}>
                  <ListItemText >{value}</ListItemText>
                </ListItem>
