import { Typography, Button, CardContent, CardActions, Card } from '@mui/material';

const ItemInfoPage = () => {

    const addToCart=()=>{
        console.log('addItem' + item)
    }

    const item = async (id) => {
        try {
            let res = await fetch(`${url}/${id}`);
            let item = await res.json();
            console.log(item)
            return item;
        } catch (err) {
            console.log('error:' + err)
        }

    }

    
    if(item){
       return (
        
        <Card sx={{ minWidth: 275 }}>
            <CardContent>
                <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>{item.category}</Typography>
                <Typography variant="h5" component="div">{item.title}</Typography>
                <Typography sx={{ mb: 1.5 }} color="text.secondary">{item.price}$</Typography>
                <Typography variant="body2">{item.description}
                </Typography>
            </CardContent>
            <CardActions>
                <Button size="small" onClick={(item) => addToCart(item)} >Add to cart</Button>
            </CardActions>
        </Card>
    ) 
    }else{
        <Typography>Loading...</Typography>
    }

    
}

export default ItemInfoPage
