//import components
import Header from './components/Header'
import Main from './components/Main'
import Footer from './components/Footer'
import Novedades from './components/Novedades'
import Viaje from './components/Viaje'

import { Routes, Route, Link } from 'react-router-dom'
import CssBaseline from '@mui/material/CssBaseline'
import { Box, useMediaQuery, useTheme } from '@mui/material'

function App() {

  //aca declaro el responsive. Lo paso como props
  const theme = useTheme();
  const mobile = useMediaQuery(theme.breakpoints.down('sm'));
  const tablet = useMediaQuery(theme.breakpoints.between('sm', 'md'));
  const desk = useMediaQuery(theme.breakpoints.up('md'));
  const responsive = {
    'mobile': mobile,
    'tablet': tablet,
    'desk': desk
  }
  return (
    <Box>
      <CssBaseline />
      <Header responsive={responsive} />
      <Routes>
        <Route path='/' element={<Main responsive={responsive} />} />
        <Route path='/novedades' element={<Novedades />} />
        <Route path='/viaje' element={<Viaje />} />
        <Route path='/contador' element={<Bolsos />} />
      </Routes>
      <Footer responsive={responsive} />
    </Box>
  );
}

export default App;
