import { Typography, Button, CardContent, CardActions, Card, CardMedia } from '@mui/material';
import { useParams } from 'react-router-dom';
import { useEffect,useState } from 'react';

const ItemInfoPage = ({ url }) => {

    const itemId = useParams();
    const [item, setItem] = useState({})

    const addToCart = (item) => {
        console.log('addItem' + item)
    }

    useEffect(() => {
        const getItem = async () => {
            try {
                let res = await fetch(`${url}/${itemId.id}`);
                let item = await res.json();
                console.log(item)
                setItem(item);
            } catch (err) {
                console.log('error:' + err)
            }
        }
        getItem();
    }, [itemId,url])


    if (item) {
        return (

            <Card sx={{ height: 800}}>
                <CardContent sx={{ height: 'inherit', width: '50%'}}>
                    <Typography variant='body' gutterBottom>{item.category}</Typography>
                    <CardMedia
                    component="img"
                    height="70%"
                    image={item.image}
                    alt={item.title}
                    sx={{
                        '&.MuiCardMedia-img': {
                            objectFit: 'contain',
                            height: '100%',
                            width: '100%',
                        }
                    }}
                />
                    <Typography variant="h3" component="div">{item.title}</Typography>
                    <Typography sx={{ mb: 1.5 }} >{item.price}$</Typography>
                    <Typography variant="body">{item.description}
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button size="small" onClick={(item) => addToCart(item)} >Add to cart</Button>
                </CardActions>
            </Card>
        )
    } else {
        <Typography>Loading...</Typography>
    }


}

export default ItemInfoPage
