import { AppBar, Toolbar, IconButton, Typography, List, ListItem, Grid, Menu, MenuItem } from "@mui/material"
import MenuIcon from '@mui/icons-material/Menu';
import { Box } from "@mui/system";
import { ShoppingBagOutlined, PersonOutlineOutlined, FavoriteBorderOutlined } from '@mui/icons-material'



const HeaderMobile = ({ title, navMenu }) => {

    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <AppBar component='div' color='inherit' sx={{ boxShadow: 'none', position: 'static' }}>
            <Toolbar disableGutters>
                <Grid container spacing={2}
                    sx={{
                        justifyContent: 'space-between',
                        alignItems: 'center',
                        padding: '16px',
                        margin: '0',
                        '&>.MuiGrid-item': {
                            padding: 0
                        }
                    }}>
                    <Grid item xs={1}>
                        <Box sx={{ display: "flex", flexDirection: "row nowrap" }}>
                            <IconButton>
                                <MenuIcon />
                                <Menu
                                    id="basic-menu"
                                    anchorEl={anchorEl}
                                    open={open}
                                    onClose={handleClose}
                                    MenuListProps={{
                                        'aria-labelledby': 'basic-button',
                                    }}
                                >
                                    <MenuItem onClick={handleClose}>{navMenu[0]}</MenuItem>
                                    <MenuItem onClick={handleClose}>{navMenu[1]}</MenuItem>
                                    <MenuItem onClick={handleClose}>{navMenu[2]}</MenuItem>
                                </Menu>
                            </IconButton>
                        </Box>
                    </Grid>
                    <Grid item xs={7}>
                        <Box>
                            <Typography component="h1" sx={{
                                textAlign: 'center', textTransform: 'uppercase',
                                fontFamily: 'Aldrich', letterSpacing: '6px', fontSize: '3.2vw'
                            }}>{title}</Typography>
                        </Box>
                    </Grid>
                    <Grid item xs={4}>
                        <Box>
                            <List sx={{ display: "flex", flexDirection: "row nowrap" }}>
                                <ListItem>
                                    <IconButton>
                                        <FavoriteBorderOutlined />
                                    </IconButton>
                                </ListItem>
                                <ListItem>
                                    <IconButton>
                                        <PersonOutlineOutlined />
                                    </IconButton>
                                </ListItem>
                                <ListItem>
                                    <IconButton>
                                        <ShoppingBagOutlined />
                                    </IconButton>
                                </ListItem>
                            </List>
                        </Box>
                    </Grid>
                </Grid>
            </Toolbar>

        </AppBar>
    )
}

export default HeaderMobile
