import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';

import LoadingBar from './LoadingBar';
import ItemCard from './ItemCard';

import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { cartItems } from '../../features/cart';


const ItemInfoPage = () => {

    const items = useSelector((state)=>state.items.value)
    const dispatch=useDispatch();
    

    const itemId = useParams();
    const [item, setItem] = useState(null)
    
    const [currency] = useState('$')

    const addToCart = (item) => {
        const exists = items.find((product)=>{return Number(product.id)===Number(item.id)})
        console.log(exists)
        if(exists){
            alert('PRODUCTO YA SELECCIONADO (de momento no se controlas cantidades ni se compra mas de una unidad')
        }else{
           dispatch(cartItems(item)) 
        }
        
    }

    useEffect(() => {
        setItem(items.find(item=>item.id===Number(itemId.id)))
    }, [itemId,item])


    if (item) {
        return (
            <ItemCard item={item} addToCart={addToCart} currency={currency}/>
        )
    } else {
        return <LoadingBar/>
    }


}

export default ItemInfoPage
