//import components
import Header from './components/Header'
import Main from './components/sectionComponents/Main'
import Footer from './components/Footer'
import Viaje from './components/sectionComponents/Viaje'
import Bolsos from './components/sectionComponents/Bolsos'
import Carteras from './components/sectionComponents/Carteras'
import Accesorios from './components/sectionComponents/Accesorios'
import Hombre from './components/sectionComponents/Hombre'
import Productos from './components/sectionComponents/Productos'
import ItemInfoPage from './components/styledComponent/ItemInfoPage'

import { Routes, Route } from 'react-router-dom'
import CssBaseline from '@mui/material/CssBaseline'
import { Box, useMediaQuery, useTheme } from '@mui/material'


function App() {

  const url = 'https://fakestoreapi.com/products'
  //aca declaro el responsive. Lo paso como props
  const theme = useTheme();
  const mobile = useMediaQuery(theme.breakpoints.down('sm'));
  const tablet = useMediaQuery(theme.breakpoints.between('sm', 'md'));
  const desk = useMediaQuery(theme.breakpoints.up('md'));
  const responsive = {
    'mobile': mobile,
    'tablet': tablet,
    'desk': desk
  };

  return (
    <Box id='html' sx={{width:'100%'}}>
      <CssBaseline />
      <Header responsive={responsive} />
      <Routes>
        <Route path='/' element={<Main responsive={responsive} />} />
        <Route path='/home' element={<Main responsive={responsive} />} />
        <Route path='/viaje' element={<Viaje />} />
        <Route path='/bolsos' element={<Bolsos />} />
        <Route path='/carteras' element={<Carteras />} />
        <Route path='/accesorios' element={<Accesorios/>}/>
        <Route path='/hombre' element={<Hombre/>}/>
        <Route path='/productos' element={<Productos url={url}/>}/>
        <Route path={`/productos/:id`} element={<ItemInfoPage url={url}/>}/>

      </Routes>
      <Footer responsive={responsive} />
    </Box>
  );
}

export default App;
