import { Typography, List, ListItem, IconButton, Grid, InputAdornment, TextField } from '@mui/material'
import SearchIcon from '@mui/icons-material/Search';
import {
  SearchOutlined, LocationOnOutlined, LocalPostOfficeOutlined, ShoppingBagOutlined,
  PersonOutlineOutlined, FavoriteBorderOutlined
} from '@mui/icons-material'
import { useState } from 'react';
import { useToggle } from '../../hooks/useToggle';
import SearchBar from '../styledComponent/SearchBar';


const MediumHeader = ({ title }) => {

  const { status: openSearch, toggleStatus: setOpenSearch } = useToggle(false)
  const [searchTextField, setSearchTextField] = useState('')

  function handleClickSearch(value) {
    console.log(value)
    setSearchTextField('')
    setOpenSearch(false)
  }

  return (

    <Grid container spacing={2}
      sx={{
        alignItems: 'center',
        padding: '16px',
        margin: '0',
        '&>.MuiGrid-item': {
          padding: 0
        }
      }}>
      <Grid item xs={2}>
        {!openSearch ?
          <List sx={{ display: "flex", flexDirection: "row", alignContent: "center", width: '50%' }}>
            <ListItem>
              <IconButton>
                <LocationOnOutlined />
              </IconButton>
            </ListItem>
            <ListItem>
              <IconButton onClick={() => setOpenSearch(true)}>
                <SearchOutlined />
              </IconButton>
            </ListItem>
          </List>
          :

          // refactorizar el textField aca y en tablet

          <SearchBar
            value={searchTextField}
            onChange={(e) => setSearchTextField(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === "Enter") {
                handleClickSearch(searchTextField)
              }
            }}
            InputProps={{
              endAdornment: (
                  <InputAdornment position="end" onClick={() => handleClickSearch(input)} >
                      <SearchIcon />
                  </InputAdornment>
              ),
          }}
          />
        }
      </Grid>

      <Grid item xs={8}>
        <Typography component="h1" sx={{
          textAlign: 'center', textTransform: 'uppercase',
          fontFamily: 'Aldrich', letterSpacing: '6px', fontSize: '3.2vw'
        }}>{title}</Typography>
      </Grid>

      <Grid item xs={2}>
        <List sx={{ display: "flex", flexDirection: "row" }}>
          <ListItem>
            <IconButton>
              <LocalPostOfficeOutlined />
            </IconButton>
          </ListItem>
          <ListItem>
            <IconButton>
              <FavoriteBorderOutlined />
            </IconButton>
          </ListItem>
          <ListItem>
            <IconButton>
              <PersonOutlineOutlined />
            </IconButton>
          </ListItem>
          <ListItem>
            <IconButton>
              <ShoppingBagOutlined />
            </IconButton>
          </ListItem>
        </List>
      </Grid>

    </Grid>
  )
}

export default MediumHeader
