import { useState } from "react"


const Accordion = ()=>{

    const[expand,setExpand]=useState(false);

    const toogleExpand = ()=>{
        setExpand(prevExpand=> !prevExpand)
    }

    console.log(expand)

    return(
        <div>
            <button onClick={toogleExpand}>
                Open
            </button>
            <div>
                this is the content
            </div>
        </div>
    )                  
    
}

export default Accordion
