import React, { useState } from 'react'
import { Paper } from '@mui/material'

const TopHeader = ({text1,text2,text3}) => {

  const[text, setText]=useState(text1);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (text===text1){
        setText(text2)
      }else if((text===text2)){
        setText(text3)
      }else{
        setText(text1)
      }
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <Paper elevation={1}>
      <p>{text}</p>
    </Paper>
  )
}

export default TopHeader
