import { Typography, Button, Grid, Card, CardMedia } from '@mui/material';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { Box } from '@mui/system';

const ItemInfoPage = ({ url }) => {

    const itemId = useParams();
    const [item, setItem] = useState({})

    const addToCart = (item) => {
        console.log('addItem' + item)
    }

    useEffect(() => {
        const getItem = async () => {
            try {
                let res = await fetch(`${url}/${itemId.id}`);
                let item = await res.json();
                console.log(item)
                setItem(item);
            } catch (err) {
                console.log('error:' + err)
            }
        }
        getItem();
    }, [itemId, url])


    if (item) {
        return (
            <Card sx={{ height: '800px'}}>
                <Box sx={{ height: '10%' }}>
                    <Typography variant='body' gutterBottom>{item.category}</Typography>
                </Box>
                <Grid container spacing={2} sx={{height:'90%'}}>
                    <Grid item xs={6}>
                        <Box sx={{ height: '50%', width: "45%" }}>
                            <CardMedia
                                component="img"
                                image={item.image}
                                alt={item.title}
                                sx={{
                                    '&.MuiCardMedia-img': {
                                        objectFit: 'contain',
                                        height: '100%',
                                        width: '100%',
                                    }
                                }}
                            />
                        </Box>
                    </Grid>
                    <Grid item xs={6}>
                        <Box sx={{ height: '20%' }}>
                            <Typography variant="h3" component="div">{item.title}</Typography>
                        </Box>
                        <Box sx={{ height: '10%' }}>
                            <Typography sx={{ mb: 1.5 }} >{item.price}$</Typography>
                            <Button size="small" onClick={(item) => addToCart(item)} >Add to cart</Button>
                        </Box>
                        <Box sx={{ height: '30%' }}>
                            <Typography variant="body">{item.description}</Typography>
                        </Box>
                    </Grid>

                </Grid>

            </Card>
        )
    } else {
        <Typography>Loading...</Typography>
    }


}

export default ItemInfoPage
